.btn-authentication:hover {
  background-color: white;
  color: #ff6b08;
}

.modal__btn {
  position: absolute;
  display: block;
  right: 25px;
  top: 25px;
  width: 30px;
  height: 30px;
  padding: 0;
  background-color: transparent;
  border: none;
  cursor: pointer;
}

.modal__btn_close::before {
  transform: rotate(-45deg);
  transition: transform 300ms linear;
  position: absolute;
  display: block;
  content: '';
  width: 30px;
  height: 3px;
  background-color: black;

}

.modal__btn:hover .modal__btn_close::before {
  transform: rotate(-130deg);
    background-color: #ff6b08;
}

.modal__btn:hover .modal__btn_close::after {
  transform: rotate(130deg);
  background-color: #ff6b08;
}

.modal__btn_close::after {
  transform: rotate(45deg);
  transition: transform 300ms linear;
  position: absolute;
  display: block;
  content: '';
  width: 30px;
  height: 3px;
  background-color: black;
}

