.header {
  padding-top: 46px;
  padding-bottom: 46px;
  height: 230px;
  position: relative;
  margin: 0 auto;

  @media screen and (min-width: 768px) {
    padding-top: 41px;
    padding-bottom: 41px;
  }
}

.header-container_home {
  background-image: linear-gradient(rgba(0, 0, 0, 0.56), rgba(0, 0, 0, 0.56)),
    url('../images/bg-images-main/mobile.jpg');
  width: 100%;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;

  @media screen and (min-device-pixel-ratio: 2),
  screen and (min-resolution: 192 dpi),
  screen and (min-resolution: 2dppx) {
    background-image: linear-gradient(rgba(0, 0, 0, 0.56), rgba(0, 0, 0, 0.56)),
      url('../images/bg-images-main/mobile@2x.jpg');
  }

  @media screen and (min-width: 768px) {
    background-image: linear-gradient(rgba(0, 0, 0, 0.56), rgba(0, 0, 0, 0.56)),
      url('../images/bg-images-main/tablet.jpg');

    @media screen and (min-device-pixel-ratio: 2),
    screen and (min-resolution: 192 dpi),
    screen and (min-resolution: 2dppx) {
      background-image: linear-gradient(rgba(0, 0, 0, 0.56), rgba(0, 0, 0, 0.56)),
        url('../images/bg-images-main/tablet@2x.jpg');
    }
  }

  @media screen and (min-width: 1024px) {
    background-image: linear-gradient(rgba(0, 0, 0, 0.56), rgba(0, 0, 0, 0.56)),
      url('../images/bg-images-main/desctop.jpg');

    @media screen and (min-device-pixel-ratio: 2),
    screen and (min-resolution: 192 dpi),
    screen and (min-resolution: 2dppx) {
      background-image: linear-gradient(rgba(0, 0, 0, 0.56), rgba(0, 0, 0, 0.56)),
        url('../images/bg-images-main/desctop@2x.jpg');
    }
  }
}

.header-container_library {
  background-image: linear-gradient(rgba(0, 0, 0, 0.56), rgba(0, 0, 0, 0.56)),
    url('../images/bg-images-library/mobile.jpg');
  width: 100%;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;

  @media screen and (min-device-pixel-ratio: 2),
  screen and (min-resolution: 192 dpi),
  screen and (min-resolution: 2dppx) {
    background-image: linear-gradient(rgba(0, 0, 0, 0.56), rgba(0, 0, 0, 0.56)),
      url('../images/bg-images-library/mobile@2x.jpg');
  }

  @media screen and (min-width: 768px) {
    background-image: linear-gradient(rgba(0, 0, 0, 0.56), rgba(0, 0, 0, 0.56)),
      url('../images/bg-images-library/tablet.jpg');

    @media screen and (min-device-pixel-ratio: 2),
    screen and (min-resolution: 192 dpi),
    screen and (min-resolution: 2dppx) {
      background-image: linear-gradient(rgba(0, 0, 0, 0.56), rgba(0, 0, 0, 0.56)),
        url('../images/bg-images-library/tablet@2x.jpg');
    }
  }

  @media screen and (min-width: 1024px) {
    background-image: linear-gradient(rgba(0, 0, 0, 0.56), rgba(0, 0, 0, 0.56)),
      url('../images/bg-images-library/desctop.jpg');

    @media screen and (min-device-pixel-ratio: 2),
    screen and (min-resolution: 192 dpi),
    screen and (min-resolution: 2dppx) {
      background-image: linear-gradient(rgba(0, 0, 0, 0.56), rgba(0, 0, 0, 0.56)),
        url('../images/bg-images-library/desctop@2x.jpg');
    }
  }
}

.logo-icon {
  width: 24px;
  height: 24px;
  fill: var(--header-logo-svg-fill);
  transition: fill 250ms cubic-bezier(0.4, 0, 0.2, 1);

  &:hover,
  &:focus {
    fill: var(--secondary-active-color);
  }

  @media screen and (min-width: 768px) {
    margin-right: 10px;
  }
}

.search-icon {
  width: 12px;
  height: 12px;
}

.header-nav {
  display: flex;
  justify-content: space-between;
  margin-bottom: 37px;
  align-items: center;
}

.logo-text {
  font-size: var(--logo-text-size-and-modal-header);
  line-height: 1.16px;
  color: var(--main-white-color);

  @media screen and (max-width: 767px) {
    display: none;
  }
}

.navigation-menu-list {
  display: flex;
}

.navigation-menu-list :not(:first-child) {
  margin-left: 39px;
}

.navigation-menu-link {
  position: relative;
  font-size: var(--main-text-size);
  line-height: 1.16px;
  height: 14px;
  display: flex;
  align-items: center;
  text-align: center;
  text-transform: var(--text-big-letters);
  color: var(--main-white-color);
  transition: color 250ms cubic-bezier(0.4, 0, 0.2, 1);

  &:hover,
  &:focus {
    color: var(--main-active-color);
  }
}

.current::after {
  content: '';
  position: absolute;
  left: 0;
  bottom: -3px;
